import java.util.ArrayList;
import java.util.List;

import acsse.csc2a.model.Warrior;
import acsse.csc2a.tester.PB2Tester;
import acsse.csc2a.model.Mage;
import acsse.csc2a.model.Rogue;
import acsse.csc2a.visitor.AbstractCharacterVisitable;
import acsse.csc2a.visitor.BuffVisitor;

