public class Main {

	public static void main(String[] args) {
		List<AbstractCharacterVisitable> characters = new ArrayList<>();
		characters.add(new Warrior(50));
		characters.add(new Mage(50));
		characters.add(new Rogue(9));

		BuffVisitor buffVisitor = new BuffVisitor();
		for (AbstractCharacterVisitable character : characters) {
			character.accept(buffVisitor);
		}

		PB2Tester.Test();
	}
}
